function createElement (parent, tag, c = false, text = false, attributes = false, style = null) {
    let el = {};
    try {
        el = document.createElement(tag);
    } catch (e) {
        return false;
    }

    let parentEl = null;
    try {
        if (parent) {
            if (typeof parent === 'string') parentEl = document.querySelector(parent);
            else parentEl = parent;

            if (parentEl) parentEl.appendChild(el);
        }
        if (c) el.className = c; 
        if (text) el.innerText = text;
        if (attributes) {
            for (const [key, value] of Object.entries(attributes)) {
                el.setAttribute (key, value);
            };
        }
        if (style) {
            for (const [key, value] of Object.entries(style)) {
                el.style[key] = value;
            };
        }
    } catch (e) {
        console.error(e);
        return false;
    }

    return el
}

function createHTMLElement (parent, tag, c = false, html = false, attributes = false, style = null) {
    let el = {};
    try {
        el = document.createElement(tag);
    } catch (e) {
        return false;
    }

    let parentEl = null;
    try {
        if (parent) {
            if (typeof parent === 'string') parentEl = document.querySelector(parent);
            else parentEl = parent;

            if (parentEl) parentEl.appendChild(el);
        }
        if (c) el.className = c; 
        if (html) el.innerHTML = html;
        if (attributes) {
            for (const [key, value] of Object.entries(attributes)) {
                el.setAttribute (key, value);
            };
        }
        if (style) {
            for (const [key, value] of Object.entries(style)) {
                el.style[key] = value;
            };
        }
    } catch (e) {
        console.error(e);
        return false;
    }

    return el
}

