function isAuthenticatedUser ()  {
    if (typeof cookies['pymnts-device-auth'] !== 'undefined' && 
            typeof cookies['pymnts-browser-id'] !== 'undefined' && 
            cookies['pymnts-device-auth'] === cookies['pymnts-browser-id']) {
                return true;
    }
    if (typeof cookies['pymnts-authenticated-user'] !== 'undefined') return true;
    return false;
}

